import React, { Component } from 'react'
import { movies } from './getmovies'

export default class Movies extends Component {
    render() {
        let movie = movies.results;
        return (

            <>
                {
                    movie.length == 0 ?
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                        :
                        <div>
                            <h3 className='text-center'>Trending</h3>
                            <div>
                                {
                                    movie.map((movieobj) => (
                                        <div className="card movies-card" >
                                            <img src={`https://image.tmdb.org/t/p/original${movieobj.backdrop_path}`}  alt={movieobj.title}/>
                                            {/* <div className="card-body"> */}
                                            <h1 className="card-title movie-title">{movieobj.original_title}</h1>
                                            <p className="card-text movie-text">{movieobj.overview}</p>
                                            <a href="#" class="btn btn-primary">Go somewhere</a>
                                            {/* </div> */}
                                        </div>
                                    ))
                                }
                            </div>
                        </div>

                }
            </>

        )
    }
}
