import React, { Component } from 'react'
import { movies } from './getmovies'



export default class Banner extends Component {

    async componentDidMount(){
        //side effects
        const res = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=5d9e83e0ec9df654c0a8c9699910552a&language=en-US&page=${this.state.currPage}`);
        let data = res.data ;
        // console.log(data);
        this.setState({
            movies:[...data.results]
        })
        console.log('mounting done');
    }

    
    render() {
        // console.log(movies);

        let movie = movies[0];
        return (
            <>
                {
                    movie == '' ?
                        <div className="spinner-border text-primary" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                            :
                            <div className="card banner-card" >
                                <img src= {`https://image.tmdb.org/t/p/original${movie.backdrop_path}`} className="card-img-top banner-img" alt="..." />
                                {/* <div className="card-body"> */}
                                    <h1 className="card-title banner-title">{movie.original_title}</h1>
                                    <p className="card-text banner-text">{movie.overview}</p>
                                    {/* <a href="#" class="btn btn-primary">Go somewhere</a> */}
                                {/* </div> */}
                            </div>
                }
            </>
        )
    }
}
