import React, { Component } from 'react'
import { movies } from './getmovies'
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.2.1/css/all.min.css" integrity="sha512-MV7K8+y+gLIBoVD59lQIYicR65iaqukzvf/nwasF0nqhPay5w/9lJmVM2hMDcnK1OnMGCdVK+iQrJ7lzPJQd1w==" crossorigin="anonymous" referrerpolicy="no-referrer" />

export default class Faviourates extends Component {
    constructor(){
        super();
        this.state={
            genres:[],
            currgen:'All Genres',
            movies:[],
            currText: ``,
            limit: 5,
            currPage: 1
        }
    }

    componentDidMount(){

        let genreids = { 28: 'Action', 12: 'Adventure', 16: 'Animation', 35: 'Comedy', 80: 'Crime', 99: 'Documentary',  18: 'Drama', 10751: 'Family', 14: 'Fantasy', 36: 'History', 27: 'Horror', 10402: 'Music', 9648: 'Mystery', 10749: 'Romance', 878: 'Sci-fi', 10770: 'TV', 53: 'Thriller', 10752: 'War', 37: 'Western' };
        let data = JSON.parse(localStorage.getItem("movies-app")||"[]")
        let temp=[];
        data.forEach((movieobj)=>{
           if(!temp.includes(genreids[movieobj.genre_ids[0]])){
           temp.push(genreids[movieobj.genre_ids[0]]);
            }
        })
        temp.unshift('All Genres');
        this.setState({
            genres:[...temp],
            movies:[...data]
        })
        
    }
    handleGenreChanmger=(genre)=>{
        this.setState({
            currgen:genre
        })
    }

    sortPopularityDesc=()=>{
          let temp = this.state.movies;
          temp.sort(function(objA,objB){
               return objB.popularity-objA.popularity ; 
          })
          this.setState({
            movies:[...temp]
          })
    }
    
    sortPopularityAsc=()=>{
        let temp = this.state.movies;
        temp.sort(function(objA,objB){
             return objA.popularity-objB.popularity ; 
        })
        this.setState({
          movies:[...temp]
        })
  }
    
    sortRatingDesc=()=>{
        let temp = this.state.movies;
        temp.sort(function(objA,objB){
             return objB.vote_average-objA.vote_average ; 
        })
        this.setState({
          movies:[...temp]
        })  
    }

    sortRatingAsc=()=>{
        let temp = this.state.movies;
        temp.sort(function(objA,objB){
             return objA.vote_average-objB.vote_average ; 
        })
        this.setState({
          movies:[...temp]
        }) 
    }
    handlePageChange=(page)=>{
       this.setState({
        currPage:page
       })
    }
    
    handleDeleteClick=(id)=>{
        let newarr = [];
        newarr = this.state.movies.filter((movieobj)=> movieobj.id!==id)
        this.setState({
            movies :[...newarr]
        })
        localStorage.setItem("movies-app",JSON.stringify(newarr))
    }

  render() {
        // const movie = movies.results ;
        // console.log(movie);
    
        let genreids = { 28: 'Action', 12: 'Adventure', 16: 'Animation', 35: 'Comedy', 80: 'Crime', 99: 'Documentary',  18: 'Drama', 10751: 'Family', 14: 'Fantasy',
        36: 'History', 27: 'Horror', 10402: 'Music', 9648: 'Mystery', 10749: 'Romance', 878: 'Sci-fi', 10770: 'TV', 53: 'Thriller', 10752: 'War', 37: 'Western' };

        let filterArr = [];
       
        if(this.state.currText==''){
            filterArr = this.state.movies ;
        }
        else{
           filterArr = this.state.movies.filter((movieobj)=>{
             let title = movieobj.original_title.toLowerCase();
             return title.includes(this.state.currText.toLocaleLowerCase())

           })
        }


        // if(this.state.currgen === "All Genres"){
        //     filterArr = this.state.movies;
        // }
        if(this.state.currgen !== "All Genres"){
            filterArr = this.state.movies.filter((movieobj)=>genreids[movieobj.genre_ids[0]] == this.state.currgen)
        }

        let pages =  Math.ceil(filterArr.length/this.state.limit);
        let pagesarr = [];
        for(let  i =1 ; i<=pages ;i++){
            pagesarr.push(i);
        }
        let si = (this.state.currPage-1)*(this.state.limit) ;
        let ei = si+(this.state.limit) ;
        filterArr = filterArr.slice(si,ei);


    return (
        <div>
        <>
        <div className="main">
                <div className="row">
                    <div className="col-lg-3 col-sm-12" >
                        <ul class="list-group favourites-genres">
                        {
                            this.state.genres.map((genre)=>(
                                this.state.currgen === genre ?
                                <li class="list-group-item" style={{background:'#3f51b5', color:'white', fontWeight:'bold'}}>{genre}</li>:
                                <li class="list-group-item" style={{background:'white', color:'#3f51b5', fontWeight:'bold'}} onClick={()=>this.handleGenreChanmger(genre)}>{genre}</li>
                            ))
                        }

                        </ul>
                    </div>
                    <div className="col-lg-9 favourites-table col-sm-12">
                        <div className="row ">
                            <input type="text" className="input-group-text col" placeholder='Search' value={this.state.currText} onChange={(e)=>this.setState({ currText : e.target.value })}></input>
                            <input type="number" className="input-group-text col" placeholder='Rows Count' value ={this.state.limit}  onChange={(e)=>this.setState({limit: e.target.value})}></input>
                        </div>
                        <div className="row">
                            <table class ="table">
                                <thead>
                                    <tr>
                                        <th scope="col">Title</th>
                                        <th scope="col">Genre</th>
                                        <th scope="col"><i class="fa-solid fa-sort-up" onClick={this.sortPopularityDesc}></i>Popularity<i class="fa-solid fa-sort-down" onClick={this.sortPopularityAsc}></i></th>
                                        <th scope="col"><i class="fa-solid fa-sort-up" onClick={this.sortRatingDesc}></i>Rating<i class="fa-solid fa-sort-down"onClick={this.sortRatingAsc}></i></th>
                                    </tr>
                                </thead>
                                <tbody>

                                    {
                                        filterArr.map((movieobj)=>(
                                            <tr>
                                            <td><img src={`https://image.tmdb.org/t/p/original${movieobj.backdrop_path}`} alt={`${movieobj.original_title}`} style={{width:'4rem', height:'4rem', padding:'0.2rem'}}></img>{ movieobj.original_title}</td>
                                            <td>{genreids[movieobj.genre_ids[0]]}</td>
                                            <td>{movieobj.popularity}</td>
                                            <td>{movieobj.vote_average}</td>
                                            <td><button type="button" class="btn btn-danger" onClick={()=>this.handleDeleteClick(movieobj.id)}>Delete</button></td>
                                            </tr>
                                        ))
                                    }
                                    
                                </tbody>
                            </table>
                        </div>

                        <nav aria-label="Page navigation example">
                            <ul class="pagination">
                                {
                                    pagesarr.map((page)=>(
                                        <li class="page-item"><a class="page-link" onClick={()=>this.handlePageChange(page)}>{page}</a></li>
                                    ))
                                }
                                
                            </ul>
                        </nav>
                        

                        
                    </div>
                </div>
        </div>
        </>
        </div>
    )
  }
}
